import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../contexts/AuthContext';
import { useLocation } from '../../contexts/LocationContext';
import LocationMap from '../Map/LocationMap';
import webSocketService from '../../utils/websocket';
import './Dashboard.css';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const { isTracking, trackingError } = useLocation();
  const [patientInfo, setPatientInfo] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }

    // Connect to WebSocket
    const token = localStorage.getItem('userToken');
    if (token) {
      webSocketService.connect(token);

      // If user is a guardian, fetch patient info
      if (user.role === 'guardian' || user.role === 'doctor') {
        fetchPatientInfo(token);
      }
    }

    // Cleanup
    return () => {
      webSocketService.disconnect();
    };
  }, [navigate, user]);

  const fetchPatientInfo = async (token) => {
    try {
      const res = await axios.get('http://localhost:5000/api/patient-info', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPatientInfo(res.data);
    } catch (err) {
      console.error('Error fetching patient info:', err);
    }
  };

  const handleLogout = () => {
    webSocketService.disconnect();
    logout();
    navigate('/login');
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Welcome, {user?.name}</h1>
        <p>Role: {user?.role}</p>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </div>

      <div className="dashboard-content">
        {trackingError && (
          <div className="alert alert-danger">
            <strong>Location Error:</strong> {trackingError}
          </div>
        )}

        <div className="location-status">
          <h3>Location Sharing</h3>
          <p>Status: {isTracking ? 'Active' : 'Inactive'}</p>
        </div>

        <div className="map-section">
          <h3>Location Map</h3>
          <LocationMap />
        </div>

        {user?.role === 'patient' && (
          <div className="patient-info">
            <h3>Your Information</h3>
            <p>Your location is being shared with your guardians.</p>
          </div>
        )}

        {(user?.role === 'guardian' || user?.role === 'doctor') && patientInfo && (
          <div className="patient-info">
            <h3>Patient Information</h3>
            <p>Patient: {patientInfo.name}</p>
            <p>Age: {patientInfo.age}</p>
            <p>Medical Condition: {patientInfo.medicalCondition}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
