import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import L from 'leaflet';
import { useLocation } from '../../contexts/LocationContext';
import { useAuth } from '../../contexts/AuthContext';
import 'leaflet/dist/leaflet.css';
import './LocationMap.css';

// Fix for default marker icons in react-leaflet
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';

let DefaultIcon = L.icon({
  iconUrl: icon,
  shadowUrl: iconShadow,
  iconSize: [25, 41],
  iconAnchor: [12, 41]
});

L.Marker.prototype.options.icon = DefaultIcon;

// Custom icons for patient and guardian
const patientIcon = new L.Icon({
  iconUrl: '/markers/patient-marker.png', // You'll need to add these images to public/markers/
  shadowUrl: iconShadow,
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32]
});

const guardianIcon = new L.Icon({
  iconUrl: '/markers/guardian-marker.png',
  shadowUrl: iconShadow,
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32]
});

// Component to recenter map when locations change
const MapUpdater = ({ locations }) => {
  const map = useMap();

  useEffect(() => {
    if (locations.length === 0) return;

    // If only one location, center on it
    if (locations.length === 1) {
      map.setView([locations[0].lat, locations[0].lng], 15);
      return;
    }

    // If multiple locations, fit bounds to include all
    const bounds = L.latLngBounds(locations);
    map.fitBounds(bounds, { padding: [50, 50] });
  }, [locations, map]);

  return null;
};

const LocationMap = () => {
  const { myLocation, connectedUsers } = useLocation();
  const { user } = useAuth();
  const [allLocations, setAllLocations] = useState([]);
  const [mapCenter, setMapCenter] = useState([0, 0]);
  const [mapZoom, setMapZoom] = useState(2);

  useEffect(() => {
    const locations = [];

    // Add my location if available
    if (myLocation) {
      locations.push({
        lat: myLocation.latitude,
        lng: myLocation.longitude,
        id: 'me',
        name: `${user.name} (Me)`,
        role: user.role,
        isSelf: true
      });
    }

    // Add connected users' locations
    Object.values(connectedUsers).forEach(connectedUser => {
      if (connectedUser.coordinates) {
        locations.push({
          lat: connectedUser.coordinates.latitude,
          lng: connectedUser.coordinates.longitude,
          id: connectedUser.userId,
          name: connectedUser.userName,
          role: connectedUser.role,
          isSelf: false
        });
      }
    });

    setAllLocations(locations);

    // Set initial map center and zoom
    if (locations.length > 0) {
      if (locations.length === 1) {
        setMapCenter([locations[0].lat, locations[0].lng]);
        setMapZoom(15);
      } else {
        // Center will be adjusted by MapUpdater component
        const firstLocation = locations[0];
        setMapCenter([firstLocation.lat, firstLocation.lng]);
        setMapZoom(12);
      }
    }
  }, [myLocation, connectedUsers, user]);

  if (!myLocation && Object.keys(connectedUsers).length === 0) {
    return (
      <div className="location-map-container loading">
        <p>Waiting for location data...</p>
      </div>
    );
  }

  return (
    <div className="location-map-container">
      <MapContainer 
        center={mapCenter} 
        zoom={mapZoom} 
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {allLocations.map(location => (
          <Marker
            key={location.id}
            position={[location.lat, location.lng]}
            icon={location.role === 'patient' ? patientIcon : guardianIcon}
          >
            <Popup>
              <div>
                <strong>{location.name}</strong>
                <p>Role: {location.role}</p>
                {location.isSelf ? (
                  <p><em>This is you</em></p>
                ) : (
                  <p>Last updated: {new Date(location.timestamp).toLocaleTimeString()}</p>
                )}
              </div>
            </Popup>
          </Marker>
        ))}

        <MapUpdater locations={allLocations} />
      </MapContainer>
    </div>
  );
};

export default LocationMap;
