import React, { useState } from 'react';

const MedicationTracker = ({ medications = [], setMedications, readOnly }) => {
  const [med, setMed] = useState({ name: '', dosage: '', frequency: '' });

  const handleChange = (e) => {
    setMed({ ...med, [e.target.name]: e.target.value });
  };

  const handleAdd = (e) => {
    e.preventDefault();
    if (!med.name || !med.dosage) return;

    if (typeof setMedications === 'function') {
      setMedications([...(Array.isArray(medications) ? medications : []), med]);
    }
    setMed({ name: '', dosage: '', frequency: '' });
  };

  return (
    <div className="section">
      <h4>ðŸ’Š Medication Tracker</h4>
      {!readOnly && typeof setMedications === 'function' && (
        <form onSubmit={handleAdd} style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem' }}>
          <input
            name="name"
            placeholder="Name"
            value={med.name}
            onChange={handleChange}
            required
          />
          <input
            name="dosage"
            placeholder="Dosage"
            value={med.dosage}
            onChange={handleChange}
            required
          />
          <input
            name="frequency"
            placeholder="Frequency"
            value={med.frequency}
            onChange={handleChange}
          />
          <button type="submit">Add Medication</button>
        </form>
      )}
      {(!medications || medications.length === 0) ? (
        <p style={{ color: '#718096' }}>No medications recorded</p>
      ) : (
        <ul>
          {medications.map((m, idx) => (
            <li key={idx}>
              <b>{m.name}</b> - {m.dosage} ({m.frequency})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};


