  //<input name="name" placeholder="Name" value={med.name} onChange={handleChange} required />
  //<input name="dosage" placeholder="Dosage" value={med.dosage} onChange={handleChange} required />
  //<input name="frequency" placeholder="Frequency" value={med.frequency} onChange={handleChange} />
  //<button type="submit">Add Medication</button> {/* âœ… Submit button added */}
//</form>



<form onSubmit={handleAdd} style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem' }}>
  <input name="name" placeholder="Name" value={med.name} onChange={handleChange} required />
  <input name="dosage" placeholder="Dosage" value={med.dosage} onChange={handleChange} required />
  <input name="frequency" placeholder="Frequency" value={med.frequency} onChange={handleChange} />
  <button type="submit">Add Medication</button>
</form>








export default MedicationTracker;
