// components/PatientCard.jsx (Unchanged from previous robust version)
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import MedicationTracker from './MedicationTracker'; // Import the MedicationTracker
import ActivityLog from './ActivityLog'; // Import ActivityLog

const PatientCard = ({ patientId }) => {
  const [patient, setPatient] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchPatient = async () => {
    setLoading(true);
    setError(null);
    try {
      // Make sure the URL matches your Express route for fetching a single patient
      const res = await axios.get(`http://localhost:3000/api/patients/${patientId}`);
      setPatient(res.data);
    } catch (err) {
      console.error('Error fetching patient:', err);
      setError('Failed to load patient data.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (patientId) {
      fetchPatient();
    }
  }, [patientId]);

  // Handle checkbox toggle for medications
  const handleMedicationToggle = async (medicationId, currentStatus) => {
    try {
      const response = await axios.put(
        `http://localhost:3000/api/patients/${patientId}/medications/${medicationId}`,
        { taken: !currentStatus }
      );
      setPatient(response.data); // Update the patient state with the latest data from the server
    } catch (error) {
      console.error('Error updating medication:', error);
    }
  };

  // Handle medication deletion
  const handleMedicationDelete = async (medicationId) => {
    try {
      const response = await axios.delete(
        `http://localhost:3000/api/patients/${patientId}/medications/${medicationId}`
      );
      if (response.status === 200) {
        setPatient(prevPatient => ({
          ...prevPatient,
          medications: prevPatient.medications.filter(med => med._id !== medicationId)
        }));
      }
    } catch (error) {
      console.error('Error deleting medication:', error);
    }
  };

  // Callback for MedicationTracker to update patient state when a new medication is added
  const handleNewMedicationAdded = (newMedication) => {
    setPatient((prevPatient) => {
      if (!prevPatient) return prevPatient;
      return {
        ...prevPatient,
        medications: [...(prevPatient.medications || []), newMedication],
      };
    });
  };

  // Callback for ActivityLog to update patient state when a new activity is added
  const handleNewActivityAdded = (newActivity) => {
    setPatient((prevPatient) => {
      if (!prevPatient) return prevPatient;
      return {
        ...prevPatient,
        activities: [...(prevPatient.activities || []), newActivity],
      };
    });
  };


  if (loading) return <div>Loading patient data...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!patient) return <div>No patient data available.</div>;

  return (
    <div className="max-w-md mx-auto bg-white shadow-lg rounded-xl p-6 mb-6">
      <h2 className="text-2xl font-semibold mb-2">{patient.name}</h2>
      <p className="text-gray-700">Age: {patient.age}</p>
      <p className="text-gray-700">
        DOB: {new Date(patient.dateOfBirth).toLocaleDateString()}
      </p>

      {/* Medication Section */}
      <h3 className="text-xl mt-4 mb-2 font-medium">Medications</h3>
      {patient.medications && patient.medications.length === 0 ? (
        <p style={{ color: '#718096' }}>No medications recorded</p>
      ) : (
        <ul className="space-y-2">
          {patient.medications?.map((med) => (
            <li key={med._id} className="flex items-center justify-between">
              <div className="flex items-center">
                <input
                  type="checkbox"
                  checked={med.taken}
                  onChange={() => handleMedicationToggle(med._id, med.taken)}
                  className="mr-2"
                />
                <span
                  className={`${
                    med.taken ? 'line-through text-gray-500' : 'text-black'
                  }`}
                >
                  <b>{med.name}</b> ({med.dosage}) {med.frequency && `- ${med.frequency}`}
                  {med.lastTaken && med.taken && (
                    <span className="text-sm text-gray-400 ml-2">
                      (Taken: {new Date(med.lastTaken).toLocaleTimeString()})
                    </span>
                  )}
                </span>
              </div>
              <button
                onClick={() => handleMedicationDelete(med._id)}
                className="ml-4 text-red-500 hover:text-red-700 text-sm"
                title="Delete Medication"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      )}

      {/* Medication Tracker for Adding */}
      <MedicationTracker
        patientId={patient._id} // Pass the patient's actual ID
        onMedicationAdded={handleNewMedicationAdded}
        readOnly={false} // Allow adding medications to this patient
      />

      {/* Activity Section */}
      <h3 className="text-xl mt-4 mb-2 font-medium">Activities</h3>
      {patient.activities && patient.activities.length === 0 ? (
        <p style={{ color: '#718096' }}>No activities recorded</p>
      ) : (
        <ul className="space-y-2">
          {patient.activities?.map((activity) => (
            <li key={activity._id} className="flex items-center justify-between">
              <b>{activity.type}</b> ({activity.duration}) {activity.notes && `- ${activity.notes}`}
              {activity.activityDate && (
                <span className="text-sm text-gray-400 ml-2">
                  ({new Date(activity.activityDate).toLocaleDateString()})
                </span>
              )}
              {/* Add delete button for activities if desired */}
            </li>
          ))}
        </ul>
      )}
      {/* Activity Log for Adding */}
      <ActivityLog
        patientId={patient._id} // Pass the patient's actual ID
        onActivityAdded={handleNewActivityAdded}
        readOnly={false} // Allow adding activities to this patient
      />
    </div>
  );
};

export default PatientCard;
