// components/MedicationTracker.jsx (Revised to handle both form and live add)
import React, { useState } from 'react';

const MedicationTracker = ({ medications = [], setMedications, readOnly, patientId, onMedicationAdded }) => {
  const [med, setMed] = useState({ name: '', dosage: '', frequency: '' });

  const handleChange = (e) => {
    setMed({ ...med, [e.target.name]: e.target.value });
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    if (!med.name || !med.dosage) return;

    if (patientId && onMedicationAdded) { // If it's used within PatientCard for live updates
      try {
        const response = await fetch(`http://localhost:3000/api/patients/${patientId}/medications`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(med),
        });

        if (!response.ok) throw new Error('Failed to add medication to backend');
        const newMedication = await response.json();
        onMedicationAdded(newMedication); // Notify parent (PatientCard) to update state
        setMed({ name: '', dosage: '', frequency: '' });
      } catch (error) {
        console.error('Error adding medication:', error);
      }
    } else if (typeof setMedications === 'function') { // If it's used within Dashboard form for temporary state
      setMedications([...(Array.isArray(medications) ? medications : []), { ...med, taken: false }]);
      setMed({ name: '', dosage: '', frequency: '' });
    }
  };

  return (
    <div className="section">
      <h4>ðŸ’Š Medications</h4>
      {!readOnly && (patientId || typeof setMedications === 'function') && ( // Render form if not readOnly AND it's for a patient card or for form state
        <form onSubmit={handleAdd} style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem' }}>
          <input
            name="name"
            placeholder="Name"
            value={med.name}
            onChange={handleChange}
            required
          />
          <input
            name="dosage"
            placeholder="Dosage"
            value={med.dosage}
            onChange={handleChange}
            required
          />
          <input
            name="frequency"
            placeholder="Frequency"
            value={med.frequency}
            onChange={handleChange}
          />
          <button type="submit">Add Medication</button>
        </form>
      )}
      {(!medications || medications.length === 0) ? (
        <p style={{ color: '#718096' }}>No medications recorded</p>
      ) : (
        <ul>
          {medications.map((m, idx) => (
            <li key={m._id || idx}> {/* Use m._id if available, else idx */}
              <b>{m.name}</b> ({m.dosage}) {m.frequency && `- ${m.frequency}`}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MedicationTracker;
