import React, { useState } from 'react';

const MedicationTracker = ({ medications = [], setMedications, readOnly }) => {
  const [med, setMed] = useState({
    name: '',
    dosage: '',
    times: {
      morning: false,
      lunch: false,
      dinner: false,
      emptyStomach: false
    }
  });

  const handleChange = (e) => {
    setMed({ ...med, [e.target.name]: e.target.value });
  };

  const handleTimeChange = (time) => {
    setMed({
      ...med,
      times: {
        ...med.times,
        [time]: !med.times[time]
      }
    });
  };

  const handleAdd = (e) => {
    e.preventDefault();
    if (!med.name || !med.dosage) return;

    // Check if at least one time is selected
    const hasSelectedTime = Object.values(med.times).some(time => time);
    if (!hasSelectedTime) {
      alert('Please select at least one time for the medication');
      return;
    }

    const newMedication = {
      ...med,
      id: Date.now(), // Simple ID generation
      completedTimes: {
        morning: false,
        lunch: false,
        dinner: false,
        emptyStomach: false
      },
      dateAdded: new Date().toISOString().split('T')[0]
    };

    if (typeof setMedications === 'function') {
      const newMedications = [...(Array.isArray(medications) ? medications : []), newMedication];
      setMedications(newMedications);
    }

    setMed({
      name: '',
      dosage: '',
      times: {
        morning: false,
        lunch: false,
        dinner: false,
        emptyStomach: false
      }
    });
  };

  const toggleMedicationCompletion = (medIndex, time) => {
    if (typeof setMedications === 'function') {
      const updatedMedications = medications.map((medication, index) => {
        if (index === medIndex) {
          return {
            ...medication,
            completedTimes: {
              ...medication.completedTimes,
              [time]: !medication.completedTimes[time]
            }
          };
        }
        return medication;
      });
      setMedications(updatedMedications);
    }
  };

  return (
    <div className="section">
      <h4>ðŸ’Š Medication Tracker</h4>
      {!readOnly && typeof setMedications === 'function' && (
        <div style={{ marginBottom: '0.5rem' }}>
          <div style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem' }}>
            <input
              name="name"
              placeholder="Medication Name"
              value={med.name}
              onChange={handleChange}
              style={{ flex: 1 }}
            />
            <input
              name="dosage"
              placeholder="Dosage"
              value={med.dosage}
              onChange={handleChange}
              style={{ flex: 1 }}
            />
            <input
              name="frequency"
              placeholder="Frequency"
              value={med.frequency}
              onChange={handleChange}
              style={{ flex: 1 }}
            />
            <button
              type="button"
              onClick={handleAdd}
              style={{
                background: '#2563eb',
                color: 'white',
                border: 'none',
                padding: '0.5rem 1rem',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Add
            </button>
          </div>
        </div>
      )}
      {(!medications || medications.length === 0) ? (
        <p style={{ color: '#718096', fontSize: '0.9rem' }}>No medications recorded</p>
      ) : (
        <div>
          {medications.map((m, idx) => (
            <div key={idx} style={{
              background: '#f7fafc',
              padding: '0.5rem',
              marginBottom: '0.5rem',
              borderRadius: '4px',
              borderLeft: '3px solid #48bb78'
            }}>
              <strong>{m.name}</strong> - {m.dosage}
              {m.frequency && <div style={{ fontSize: '0.9rem', color: '#718096' }}>Frequency: {m.frequency}</div>}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};






export default MedicationTracker;
