import React, { useEffect, useState } from 'react';
import api from './api/config';
import Dashboard from '.';

function App() {
  const [patients, setPatients] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    api.get('/patients')
      .then((res) => {
        setPatients(res.data);
        setError('');
      })
      .catch((err) => {
        setError('Failed to fetch patients from backend');
        console.error(err);
      })
      .finally(() => setLoading(false));
  }, []);

  return (
    <div>
      <h1 style={{ textAlign: 'center', margin: '2rem 0' }}>Caretaker Dashboard</h1>
      {loading && <div style={{ textAlign: 'center' }}>Loading patients...</div>}
      {error && <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>}
      <Dashboard patients={patients} setPatients={setPatients} />
    </div>
  );
}

export default App;
