import React, { useState } from 'react';

const MedicationTracker = ({ medications = [], setMedications, readOnly }) => {
  const [med, setMed] = useState({ name: '', dosage: '', frequency: '' });

  const handleChange = (e) => {
    setMed({ ...med, [e.target.name]: e.target.value });
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    if (!med.name || !med.dosage) return;

    try {
      const response = await fetch(`http://localhost:3000/api/patients/${patientId}/medications`, { // Assuming your server runs on 3000 and patient routes are under /api/patients
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(med),
      });

      if (!response.ok) {
        throw new Error('Failed to add medication');
      }

      const newMedication = await response.json();
      // Call the callback to update the parent component's state (PatientCard)
      if (typeof onMedicationAdded === 'function') {
        onMedicationAdded(newMedication);
      }
      setMed({ name: '', dosage: '', frequency: '' }); // Clear form
    } catch (error) {
      console.error('Error adding medication:', error);
      // Optionally display an error message to the user
    }
  };

  const handleMedicationAdded = (newMed) => {
  setMedications([...medications, newMed]);
};


  // The MedicationTracker component now only handles adding,
  // the display of the list will be handled by PatientCard
  return (
    <div className="section">
      <h4>ðŸ’Š Add New Medication</h4>
      {!readOnly && (
        <form onSubmit={handleAdd} style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem' }}>
          <input
            name="name"
            placeholder="Name"
            value={med.name}
            onChange={handleChange}
            required
          />
          <input
            name="dosage"
            placeholder="Dosage"
            value={med.dosage}
            onChange={handleChange}
            required
          />
          <input
            name="frequency"
            placeholder="Frequency"
            value={med.frequency}
            onChange={handleChange}
          />
        </form>
      )}
    </div>
  );
};


