import React, { useState, useEffect } from 'react';
//import Dashboard from './components/Dashboard';
import './App.css';
import Dashboard from './components/Dashboard';


function App() {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      // Mock data - replace with actual API call
      const mockPatients = [
        {
          id: 1,
          name: "John Smith",
          age: 78,
          condition: "Mild Dementia",
          lastCheckup: "2024-06-05",
          emergencyContact: "Mary Smith - 555-0123",
          medications: [
            {
              name: "Donepezil",
              dosage: "10mg",
              times: { morning: true, lunch: false, dinner: false, emptyStomach: false },
              completedTimes: { morning: true, lunch: false, dinner: false, emptyStomach: false },
              dateAdded: "2024-06-07"
            },
            {
              name: "Memantine",
              dosage: "20mg",
              times: { morning: true, lunch: false, dinner: true, emptyStomach: false },
              completedTimes: { morning: false, lunch: false, dinner: false, emptyStomach: false },
              dateAdded: "2024-06-07"
            }
          ],
          activities: [
            { type: "Exercise", duration: "30 min", completed: true, date: "2024-06-07" },
            { type: "Memory Games", duration: "15 min", completed: false, date: "2024-06-07" }
          ]
        },
        {
          id: 2,
          name: "Emma Johnson",
          age: 82,
          condition: "Moderate Dementia",
          lastCheckup: "2024-06-03",
          emergencyContact: "Robert Johnson - 555-0456",
          medications: [
            {
              name: "Galantamine",
              dosage: "8mg",
              times: { morning: false, lunch: true, dinner: true, emptyStomach: false },
              completedTimes: { morning: false, lunch: true, dinner: false, emptyStomach: false },
              dateAdded: "2024-06-07"
            }
          ],
          activities: [
            { type: "Music Therapy", duration: "45 min", completed: true, date: "2024-06-07" },
            { type: "Walking", duration: "20 min", completed: true, date: "2024-06-07" }
          ]
        }
      ];
      
      setPatients(mockPatients);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching patients:', error);
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Dementia Caretaker Dashboard</h1>
        <p>Comprehensive care management system</p>
      </header>
      
      {loading ? (
        <div className="loading">Loading patients...</div>
      ) : (
        <Dashboard patients={patients} setPatients={setPatients} />
      )}
    </div>
  );
}

export default App;
