import React from 'react';
import MedicationTracker from './MedicationTracker';
import ActivityLog from './ActivityLog';
import EmergencyContact from './EmergencyContact';

const PatientCard = ({ patient }) => {
  const getConditionClass = (condition) => {
    if (condition.toLowerCase().includes('mild')) return 'condition-mild';
    if (condition.toLowerCase().includes('moderate')) return 'condition-moderate';
    if (condition.toLowerCase().includes('severe')) return 'condition-severe';
    return 'condition-mild';
  };

  return (
    <div className="patient-card">
      <div className="patient-header">
        <div className="patient-info">
          <h3>{patient.name}</h3>
          <p>Age: {patient.age}</p>
          <p>Last Checkup: {patient.lastCheckup}</p>
        </div>
        <div className={`condition-badge ${getConditionClass(patient.condition)}`}>
          {patient.condition}
        </div>
      </div>

      <MedicationTracker medications={patient.medications} />
      <ActivityLog activities={patient.activities} />
      <EmergencyContact contact={patient.emergencyContact} />
    </div>
  );
};

export default PatientCard;
