import React, { useState } from 'react';

const ActivityLog = ({ activities, setActivities }) => {
  const [activity, setActivity] = useState({ type: '', duration: '', notes: '' });

  const handleChange = (e) => {
    setActivity({ ...activity, [e.target.name]: e.target.value });
  };

  const handleAdd = (e) => {
    e.preventDefault();
    if (!activity.type || !activity.duration) return;
    setActivities([...activities, activity]);
    setActivity({ type: '', duration: '', notes: '' });
  };

  return (
    <div className="section">
      <h4>üìã Daily Activities</h4>
      <form onSubmit={handleAdd} style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem' }}>
        <input
          name="type"
          placeholder="Type"
          value={activity.type}
          onChange={handleChange}
          required
        />
        <input
          name="duration"
          placeholder="Duration"
          value={activity.duration}
          onChange={handleChange}
          required
        />
        <input
          name="notes"
          placeholder="Notes"
          value={activity.notes}
          onChange={handleChange}
        />
        <button type="submit">Add</button>
      </form>
      {activities.length === 0 ? (
        <p style={{ color: '#718096' }}>No activities recorded</p>
      ) : (
        activities.map((activity, index) => (
          <div 
            key={index} 
            className={`activity-item ${activity.completed ? 'activity-completed' : 'activity-pending'}`}
          >
            <h5>{activity.type}</h5>
            <p>Duration: {activity.duration}</p>
            <p>Date: {activity.date}</p>
            <p>Status: {activity.completed ? '‚úÖ Completed' : '‚è≥ Pending'}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default ActivityLog;
