import React, { useState } from 'react';

const ActivityLog = ({ activities = [], setActivities, readOnly }) => {
  const [activity, setActivity] = useState({ type: '', duration: '', notes: '' });

  const handleChange = (e) => {
    setActivity({ ...activity, [e.target.name]: e.target.value });
  };

  const handleAdd = () => {
    if (!activity.type || !activity.duration) {
      alert('Please fill in both Activity Type and Duration');
      return;
    }

    if (typeof setActivities === 'function') {
      const newActivities = [...(Array.isArray(activities) ? activities : []), activity];
      setActivities(newActivities);
    }
    setActivity({ type: '', duration: '', notes: '' });
  };

  return (
    <div className="section">
      <h4>üìù Activity Log</h4>
      {!readOnly && typeof setActivities === 'function' && (
        <div style={{ marginBottom: '0.5rem' }}>
          <div style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem' }}>
            <input
              name="type"
              placeholder="Activity Type"
              value={activity.type}
              onChange={handleChange}
              style={{ flex: 1 }}
            />
            <input
              name="duration"
              placeholder="Duration"
              value={activity.duration}
              onChange={handleChange}
              style={{ flex: 1 }}
            />
            <input
              name="notes"
              placeholder="Notes (optional)"
              value={activity.notes}
              onChange={handleChange}
              style={{ flex: 1 }}
            />
            <button
              type="button"
              onClick={handleAdd}
              style={{
                background: '#2563eb',
                color: 'white',
                border: 'none',
                padding: '0.5rem 1rem',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Add
            </button>
          </div>
        </div>
      )}
      {(!activities || activities.length === 0) ? (
        <p style={{ color: '#718096', fontSize: '0.9rem' }}>No activities recorded</p>
      ) : (
        <div>
          {activities.map((a, idx) => (
            <div key={idx} style={{
              background: '#f7fafc',
              padding: '0.5rem',
              marginBottom: '0.5rem',
              borderRadius: '4px',
              borderLeft: '3px solid #4299e1'
            }}>
              <strong>{a.type}</strong> - {a.duration}
              {a.notes && <div style={{ fontSize: '0.9rem', color: '#718096' }}>{a.notes}</div>}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};








export default ActivityLog;
