import React, { useState } from 'react';
import '../App.css';
import ActivityLog from './ActivityLog';
import MedicationTracker from './MedicationTracker';

const initialForm = {
  id: null,
  name: '',
  age: '',
  condition: '',
  lastCheckup: '',
  emergencyContact: ''
};

const Dashboard = ({ patients, setPatients }) => {
  const [form, setForm] = useState(initialForm);
  const [showForm, setShowForm] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  // Store activities and medications per patient ID
  const [activities, setActivities] = useState({});
  const [medications, setMedications] = useState({});

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAddClick = () => {
    setForm(initialForm);
    setIsEditing(false);
    setShowForm(true);
  };

  const handleEdit = (patient) => {
    setForm(patient);
    setIsEditing(true);
    setShowForm(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      setPatients(patients.map(p => p.id === form.id ? { ...form } : p));
    } else {
      const newPatient = {
        ...form,
        id: patients.length ? Math.max(...patients.map(p => p.id)) + 1 : 1,
      };
      setPatients([...patients, newPatient]);
    }
    setShowForm(false);
    setForm(initialForm);
    setIsEditing(false);
  };

  const handleCancel = () => {
    setShowForm(false);
    setForm(initialForm);
    setIsEditing(false);
  };

  // Helper functions to update activities/medications for a patient
  const updateActivities = (patientId, newActivities) => {
    setActivities(prev => ({ ...prev, [patientId]: newActivities }));
  };
  const updateMedications = (patientId, newMedications) => {
    setMedications(prev => ({ ...prev, [patientId]: newMedications }));
  };

  return (
    <div className="dashboard">
      <button className="add-patient-btn" onClick={handleAddClick}>
        + Add New Patient
      </button>

      {showForm && (
        <form className="patient-form" onSubmit={handleSubmit}>
          <h2>{isEditing ? 'Edit Patient' : 'Add New Patient'}</h2>
          <label>
            Name:
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={form.name}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Age:
            <input
              type="number"
              name="age"
              placeholder="Age"
              value={form.age}
              onChange={handleChange}
              required
              min="0"
            />
          </label>
          <label>
            Condition:
            <input
              type="text"
              name="condition"
              placeholder="Condition"
              value={form.condition}
              onChange={handleChange}
            />
          </label>
          <label>
            Last Checkup:
            <input
              type="date"
              name="lastCheckup"
              placeholder="Last Checkup"
              value={form.lastCheckup}
              onChange={handleChange}
            />
          </label>
          <label>
            Emergency Contact:
            <input
              type="text"
              name="emergencyContact"
              placeholder="Emergency Contact"
              value={form.emergencyContact}
              onChange={handleChange}
            />
          </label>
          <div style={{ margin: '1rem 0' }}>
            <ActivityLog
              patientId={form.id || 'new'}
              activities={activities[form.id || 'new'] || []}
              setActivities={acts => updateActivities(form.id || 'new', acts)}
            />
            <MedicationTracker
              patientId={form.id || 'new'}
              medications={medications[form.id || 'new'] || []}
              setMedications={meds => updateMedications(form.id || 'new', meds)}
            />
          </div>
          <div className="form-actions">
            <button type="submit">{isEditing ? 'Save Changes' : 'Add Patient'}</button>
            <button type="button" onClick={handleCancel} className="cancel-btn">
              Cancel
            </button>
          </div>
        </form>
      )}

      <div className="patients-grid">
        {patients.map(patient => (
          <div key={patient.id} className="patient-card">
            <h3>{patient.name}</h3>
            <p><b>Age:</b> {patient.age}</p>
            <p><b>Condition:</b> {patient.condition}</p>
            <p><b>Last Checkup:</b> {patient.lastCheckup}</p>
            <p><b>Emergency Contact:</b> {patient.emergencyContact}</p>
            <button onClick={() => handleEdit(patient)}>Edit</button>
            {/* ActivityLog and MedicationTracker for this patient */}
            <div style={{ marginTop: '1rem' }}>
              <ActivityLog
                patientId={patient.id}
                activities={activities[patient.id] || []}
                setActivities={acts => updateActivities(patient.id, acts)}
              />
              <MedicationTracker
                patientId={patient.id}
                medications={medications[patient.id] || []}
                setMedications={meds => updateMedications(patient.id, meds)}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
