import React, { useState } from 'react';

const ActivityLog = ({ activities = [], setActivities, readOnly }) => {
  const [activity, setActivity] = useState({ type: '', duration: '', notes: '' });

  const handleChange = (e) => {
    setActivity({ ...activity, [e.target.name]: e.target.value });
  };

  const handleAdd = (e) => {
    e.preventDefault();
    if (!activity.type || !activity.duration) return;
    if (typeof setActivities === 'function') {
      setActivities([...(Array.isArray(activities) ? activities : []), activity]);
    }
    setActivity({ type: '', duration: '', notes: '' });
  };

  return (
    <div className="section">
      <h4>üìù Activity Log</h4>
      {!readOnly && typeof setActivities === 'function' && (
        <form onSubmit={handleAdd} style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem' }}>
          <input
            name="type"
            placeholder="Type"
            value={activity.type}
            onChange={handleChange}
            required
          />
          <input
            name="duration"
            placeholder="Duration"
            value={activity.duration}
            onChange={handleChange}
            required
          />
          <input
            name="notes"
            placeholder="Notes"
            value={activity.notes}
            onChange={handleChange}
          />
        </form>
      )}
      {(!activities || activities.length === 0) ? (
        <p style={{ color: '#718096' }}>No activities recorded</p>
      ) : (
        <ul>
          {activities.map((a, idx) => (
            <li key={idx}>
              <b>{a.type}</b> ({a.duration}) {a.notes && `- ${a.notes}`}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};


//<form onSubmit={handleAdd} style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem' }}>
  //<input name="type" placeholder="Type" value={activity.type} onChange={handleChange} required />
  //<input name="duration" placeholder="Duration" value={activity.duration} onChange={handleChange} required />
  //<input name="notes" placeholder="Notes" value={activity.notes} onChange={handleChange} />
  //<button type="submit">Add Activity</button> {/* ‚úÖ Submit button added */}
//</form>


<form onSubmit={handleAdd} style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem' }}>
  <input name="type" placeholder="Type" value={activity.type} onChange={handleChange} required />
  <input name="duration" placeholder="Duration" value={activity.duration} onChange={handleChange} required />
  <input name="notes" placeholder="Notes" value={activity.notes} onChange={handleChange} />
  <button type="submit">Add Activity</button>
</form>









export default ActivityLog;
