import React, { useEffect, useState } from 'react';
import api from './api/config';
import Dashboard from './components/Dashboard';
import './App.css';

function App() {
  const [patients, setPatients] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    api.get('/patients')
      .then((res) => {
        setPatients(res.data);
        setError('');
      })
      .catch((err) => {
        setError('Failed to fetch patients from backend');
        console.error(err);
      })
      .finally(() => setLoading(false));
  }, []);

  return (
    <div className="app-container">
      <h1 className="app-title">Caretaker Dashboard</h1>
      {loading && <div className="app-message loading">Loading patients...</div>}
      {error && <div className="app-message error">{error}</div>}
      <Dashboard patients={patients} setPatients={setPatients} />
    </div>
  );
}

export default App;
