import React, { useState } from 'react';
import ActivityLog from './ActivityLog.jsx';
import MedicationTracker from './MedicationTracker';
import '../App.css';

const initialForm = {
  id: null,
  name: '',
  age: '',
  condition: '',
  lastCheckup: '',
  emergencyContact: '',
  activities: [],
  medications: []
};







const Dashboard = ({ patients, setPatients }) => {
  const [form, setForm] = useState(initialForm);
  const [showForm, setShowForm] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAddClick = () => {
    setForm(initialForm);
    setIsEditing(false);
    setShowForm(true);
  };

  const handleEdit = (patient) => {
    setForm({
      ...patient,
      activities: Array.isArray(patient.activities) ? patient.activities : [],
      medications: Array.isArray(patient.medications) ? patient.medications : []
    });
    setIsEditing(true);
    setShowForm(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      setPatients(patients.map(p => p.id === form.id ? { ...form } : p));
    } else {
      const newPatient = {
        ...form,
        id: patients.length ? Math.max(...patients.map(p => p.id)) + 1 : 1,
      };
      setPatients([...patients, newPatient]);
    }

 console.log("Updated patients:", form.activities); // ✅ confirm activities are here
  setShowForm(false);
  setForm(initialForm);
  setIsEditing(false);




    setShowForm(false);
    setForm(initialForm);
    setIsEditing(false);
  };

  const handleCancel = () => {
    setShowForm(false);
    setForm(initialForm);
    setIsEditing(false);
  };

  // Update activities/medications in form state
  const updateActivities = (acts) =>
    setForm(f => ({
      ...(typeof f === 'object' && f !== null ? f : initialForm),
      activities: Array.isArray(acts) ? [...acts] : []
    }));

  const updateMedications = (meds) =>
    setForm(f => ({
      ...(typeof f === 'object' && f !== null ? f : initialForm),
      medications: Array.isArray(meds) ? [...meds] : []
    }));

  return (
    <div className="dashboard">
      <button className="add-patient-btn" onClick={handleAddClick}>
        + Add New Patient
      </button>

      {showForm && (
        <form className="patient-form" onSubmit={handleSubmit}>
          <h2>{isEditing ? 'Edit Patient' : 'Add New Patient'}</h2>
          <label>
            Name:
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={form.name}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Age:
            <input
              type="number"
              name="age"
              placeholder="Age"
              value={form.age}
              onChange={handleChange}
              required
              min="0"
            />
          </label>
          <label>
            Condition:
            <input
              type="text"
              name="condition"
              placeholder="Condition"
              value={form.condition}
              onChange={handleChange}
            />
          </label>
          <label>
            Last Checkup:
            <input
              type="date"
              name="lastCheckup"
              placeholder="Last Checkup"
              value={form.lastCheckup}
              onChange={handleChange}
            />
          </label>
          <label>
            Emergency Contact:
            <input
              type="text"
              name="emergencyContact"
              placeholder="Emergency Contact"
              value={form.emergencyContact}
              onChange={handleChange}
            />
          </label>
          <div style={{ margin: '1rem 0' }}>
            <div style={{ background: '#f0f0f0', padding: '0.5rem', marginBottom: '1rem', fontSize: '0.8rem' }}>
              
            </div>
           <ActivityLog
              activities={Array.isArray(form.activities) ? form.activities : []}
              setActivities={updateActivities}
            />












            
            <MedicationTracker
              medications={Array.isArray(form.medications) ? form.medications : []}
              setMedications={updateMedications}
            />
          </div>
          <div className="form-actions">
            <button type="submit">{isEditing ? 'Save Changes' : 'Add Patient'}</button>
            <button type="button" onClick={handleCancel} className="cancel-btn">
              Cancel
            </button>
          </div>
        </form>
      )}

      <div className="patients-grid">
        {patients.map(patient => (
          <div key={patient.id} className="patient-card">
            <h3>{patient.name}</h3>
            <p><b>Age:</b> {patient.age}</p>
            <p><b>Condition:</b> {patient.condition}</p>
            <p><b>Last Checkup:</b> {patient.lastCheckup}</p>
            <p><b>Emergency Contact:</b> {patient.emergencyContact}</p>
            <button onClick={() => handleEdit(patient)}>Edit</button>
            <div style={{ marginTop: '1rem' }}>
              <ActivityLog
                activities={Array.isArray(patient.activities) ? patient.activities : []}
                setActivities={() => {}}
                readOnly
              />
              <MedicationTracker
                medications={Array.isArray(patient.medications) ? patient.medications : []}
                setMedications={() => {}}
                readOnly
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
